import Cookies from 'js-cookie';
import API from './API';

export const getCookie = () => {
  const cookie = Cookies.get('userId')
  console.log(cookie)}

export const authUser = (cb) => {
    const cookie = Cookies.get('userId');
    console.log(cookie);
    API.loginUser(cookie)
      .then( () => cb())
      .catch( e => e);
  }

export const logOut = () => {
  Cookies.remove()
}
